import { Component, OnInit } from '@angular/core';
import { GenesysCloudService } from './genesys-cloud.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'Yuri\'s Angular App';
  isAuthorized = false;

  constructor(
    private genesysCloudService : GenesysCloudService,
    private route: ActivatedRoute,
  ){ }

  ngOnInit(){
    console.log(this.route.snapshot)
    this.genesysCloudService.isAuthorized.subscribe(isAuthorized => {
      this.isAuthorized = isAuthorized;
    });

    // Get query parameters for 'environment' and 'language'
    this.route.queryParams.subscribe(params => {
      const language = params['language'];
      const environment = params['environment'];

      this.genesysCloudService.setLanguage(language);
      this.genesysCloudService.setEnvironment(environment);

      this.genesysCloudService.initialize()
        .subscribe(() => { 
          console.log('Succesfully logged in.')
        });
    });    
  }
}
<div class="header">
  <img src="assets/yuri.yeti.png" alt="Yuri Smiling" srcset=""><h1>{{title}}</h1>
</div>

<nav id="main-nav">
  <ul>
    <li><a routerLink="/home">Home</a></li>
    <li><a routerLink="/agentmanager">User Search</a></li>
    <li><a routerLink="/queues">Queues List</a></li>
  </ul>
</nav>

<main>
  <div *ngIf="isAuthorized; else notAuthorized">
    <router-outlet></router-outlet>
  </div>
  <ng-template #notAuthorized>
    <div class="spinner">
      <img src="assets/loading.svg" alt="Loading..." srcset="">
    </div>
  </ng-template>
</main>
